import React, {Component} from 'react';
import Button from './Button';

export default class ApplicantCard extends Component {

    render() {
        const {applicant} = this.props;
        let statusButtons;
        switch(applicant.applicantStatus) {
            case 'PENDING' : statusButtons = (<div class='row'>
                <div className='col-md-6'>
                    <Button title='Accept' status='success'/>
                </div>
                <div class='col-md-6'>
                    <Button title='Decline' status='danger'/>
                </div>
            </div>);
            break;
        }
        console.log(statusButtons);

        console.log(applicant);
        return (<div className="card col-lg-3 col-md-4 col-sm-12">
            <img className="card-img-top" src={applicant.applicantPicture} alt='Top profile' />
            <div class='card-body'>
                <h4 class='card-title'>
                    {applicant.applicantFirstName + " " + applicant.applicantLastName}
                </h4>
                <div class='row'>
                    <div class='col-md-6'>
                        <a href={'mailto:' + applicant.applicantEmail}>
                            <Button status='primary' title='Email'/>
                        </a>  
                    </div>
                    <div class='col-md-6'>
                        <a href={applicant.applicantLinkedin}>
                            <Button status='primary' title='LinkedIn'/>
                        </a>
                    </div>
                </div>
                {statusButtons}
            </div>
        </div>);
    }
}
